import React, { FC, useState, useEffect, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import { invitationsAPI } from '../services/api';

const InvitationAcceptance: FC = () => {
  const { invitationId, token } = useParams<{ invitationId: string; token: string }>();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState(false);

  const acceptInvitation = useCallback(async () => {
    try {
      if (invitationId) {
        await invitationsAPI.acceptInvitation(invitationId);
        setSuccess(true);
      }
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to accept invitation');
    } finally {
      setLoading(false);
    }
  }, [invitationId]);

  useEffect(() => {
    acceptInvitation();
  }, [invitationId, token, acceptInvitation]);

  return (
    <div className="min-h-screen bg-gradient-to-br from-indigo-600 to-purple-600 flex items-center justify-center p-4">
      <div className="bg-white rounded-lg p-8 max-w-md w-full text-center">
        {loading && <p className="text-gray-600">Processing invitation...</p>}
        {error && <p className="text-red-600 font-semibold">{error}</p>}
        {success && (
          <div>
            <p className="text-green-600 font-semibold mb-4">âœ“ Invitation accepted!</p>
            <p className="text-gray-600">You can now access the project.</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default InvitationAcceptance;
