import React, { FC, useState, useEffect, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { projectsAPI, analyticsAPI } from '../services/api';
import { DarkModeContext } from '../context/DarkModeContext';

interface Project {
  _id: string;
  name: string;
  description: string;
  status: string;
  startDate: string;
  endDate: string;
  budget: { estimated: number; actual: number };
  progress?: number;
  members?: any[];
}

interface FormData {
  name: string;
  description: string;
  startDate: string;
  endDate: string;
  budget: string;
}

const Dashboard: FC = () => {
  const darkModeContext = useContext(DarkModeContext);
  if (!darkModeContext) return <div>Loading...</div>;
  const { isDarkMode } = darkModeContext;

  const [projects, setProjects] = useState<Project[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showNewProject, setShowNewProject] = useState(false);
  const [analytics, setAnalytics] = useState<any>(null);
  const [formData, setFormData] = useState<FormData>({
    name: '',
    description: '',
    startDate: '',
    endDate: '',
    budget: '',
  });
  const navigate = useNavigate();

  useEffect(() => {
    fetchProjects();
    fetchAnalytics();
  }, []);

  const fetchProjects = async () => {
    try {
      setLoading(true);
      const response = await projectsAPI.getAll();
      setProjects(response.data);
    } catch (err) {
      setError('Failed to load projects');
    } finally {
      setLoading(false);
    }
  };

  const fetchAnalytics = async () => {
    try {
      const response = await analyticsAPI.getDashboardOverview();
      setAnalytics(response.data);
    } catch (err) {
      console.log('Analytics not available');
    }
  };

  const handleCreateProject = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    try {
      const projectData = {
        name: formData.name,
        description: formData.description,
        startDate: formData.startDate,
        endDate: formData.endDate,
        budget: {
          estimated: formData.budget ? parseFloat(formData.budget) : 0,
          actual: 0,
        },
        status: 'planning',
        progress: 0,
      };
      const newProject = await projectsAPI.create(projectData);
      setProjects([newProject.data, ...projects]);
      setFormData({ name: '', description: '', startDate: '', endDate: '', budget: '' });
      setShowNewProject(false);
      setError('');
    } catch (err: any) {
      setError(err.response?.data?.message || 'Failed to create project');
    }
  };

  const totalProjects = projects.length;
  const completedProjects = projects.filter(p => p.status === 'completed').length;
  const totalResources = new Set(projects.flatMap(p => p.members?.map(m => m.user?._id || m.user) || [])).size;

  const getStatusColor = (status: string): string => {
    const colors: Record<string, string> = {
      'completed': 'bg-green-100 text-green-800',
      'active': 'bg-blue-100 text-blue-800',
      'on-hold': 'bg-yellow-100 text-yellow-800',
      'planning': 'bg-gray-100 text-gray-800',
      'delayed': 'bg-yellow-100 text-yellow-800',
      'at risk': 'bg-red-100 text-red-800',
    };
    return colors[status] || colors['planning'];
  };

  if (loading) {
    return <div className="min-h-screen flex items-center justify-center">Loading...</div>;
  }

  return (
    <div className={`min-h-screen ${isDarkMode ? 'bg-gray-950' : 'bg-gray-50'} p-8`}>
      <div className="max-w-7xl mx-auto">
        <h1 className={`text-4xl font-bold ${isDarkMode ? 'text-white' : 'text-gray-900'} mb-8`}>Dashboard</h1>
        
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <div className={`${isDarkMode ? 'bg-gray-900' : 'bg-white'} p-6 rounded-lg shadow`}>
            <p className={isDarkMode ? 'text-gray-400' : 'text-gray-600'}>Total Projects</p>
            <p className="text-3xl font-bold text-indigo-600">{totalProjects}</p>
          </div>
          <div className={`${isDarkMode ? 'bg-gray-900' : 'bg-white'} p-6 rounded-lg shadow`}>
            <p className={isDarkMode ? 'text-gray-400' : 'text-gray-600'}>Completed</p>
            <p className="text-3xl font-bold text-green-600">{completedProjects}</p>
          </div>
          <div className={`${isDarkMode ? 'bg-gray-900' : 'bg-white'} p-6 rounded-lg shadow`}>
            <p className={isDarkMode ? 'text-gray-400' : 'text-gray-600'}>Team Members</p>
            <p className="text-3xl font-bold text-blue-600">{totalResources}</p>
          </div>
          <div className={`${isDarkMode ? 'bg-gray-900' : 'bg-white'} p-6 rounded-lg shadow`}>
            <p className={isDarkMode ? 'text-gray-400' : 'text-gray-600'}>Active Projects</p>
            <p className="text-3xl font-bold text-purple-600">{projects.filter(p => p.status === 'active').length}</p>
          </div>
        </div>

        {projects.length === 0 ? (
          <div className={`${isDarkMode ? 'bg-gray-900' : 'bg-white'} p-12 rounded-lg text-center`}>
            <p className={isDarkMode ? 'text-gray-400' : 'text-gray-500'}>No projects yet</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {projects.map((project) => (
              <div key={project._id} className={`${isDarkMode ? 'bg-gray-900' : 'bg-white'} p-6 rounded-lg shadow hover:shadow-lg cursor-pointer`} onClick={() => navigate(`/projects/${project._id}`)}>
                <h3 className={`text-xl font-bold mb-2 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>{project.name}</h3>
                <span className={`inline-block px-3 py-1 rounded-full text-sm font-semibold ${getStatusColor(project.status)}`}>
                  {project.status}
                </span>
                <p className={`${isDarkMode ? 'text-gray-400' : 'text-gray-600'} mt-2 line-clamp-2`}>{project.description}</p>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
