import React, { FC, useState, useEffect, useContext } from 'react';
import { DarkModeContext } from '../context/DarkModeContext';
import { projectsAPI, tasksAPI } from '../services/api';

interface SearchResult {
  type: 'project' | 'task';
  id: string;
  name?: string;
  title?: string;
  description?: string;
  status?: string;
}

const Search: FC = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [filterType, setFilterType] = useState<'all' | 'projects' | 'tasks'>('all');
  const [filterStatus, setFilterStatus] = useState('all');
  const [filterPriority, setFilterPriority] = useState('all');
  const [results, setResults] = useState<SearchResult[]>([]);
  const [loading, setLoading] = useState(false);

  const darkModeContext = useContext(DarkModeContext);

  const performSearch = async () => {
    setLoading(true);
    try {
      let allResults: SearchResult[] = [];

      if (filterType === 'all' || filterType === 'projects') {
        const projectsRes = await projectsAPI.getAll();
        const filtered = projectsRes.data
          .filter((p: any) =>
            p.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
            p.description?.toLowerCase().includes(searchTerm.toLowerCase())
          )
          .map((p: any) => ({
            type: 'project' as const,
            id: p._id,
            name: p.name,
            description: p.description,
            status: p.status,
          }));
        allResults = [...allResults, ...filtered];
      }

      if (filterType === 'all' || filterType === 'tasks') {
        const tasksRes = await tasksAPI.getByProject('all');
        const filtered = (tasksRes.data || [])
          .filter((t: any) =>
            t.title.toLowerCase().includes(searchTerm.toLowerCase())
          )
          .map((t: any) => ({
            type: 'task' as const,
            id: t._id,
            title: t.title,
            status: t.status,
          }));
        allResults = [...allResults, ...filtered];
      }

      setResults(allResults);
    } catch (err) {
      console.error('Search error:', err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (searchTerm.length > 0 || filterStatus !== 'all' || filterPriority !== 'all') {
      performSearch();
    } else {
      setResults([]);
    }
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [searchTerm, filterType, filterStatus, filterPriority]);

  if (!darkModeContext) {
    return <div>Loading...</div>;
  }

  const { isDarkMode } = darkModeContext;

  return (
    <div className={`min-h-screen ${isDarkMode ? 'bg-gray-900' : 'bg-gray-50'} p-8`}>
      <div className="max-w-4xl mx-auto">
        <h1 className={`text-4xl font-bold mb-8 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>Search</h1>

        <div className="space-y-4 mb-8">
          <input
            type="text"
            placeholder="Search projects and tasks..."
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
            className={`w-full px-4 py-3 rounded-lg border-2 ${
              isDarkMode
                ? 'bg-gray-800 border-gray-700 text-white'
                : 'bg-white border-gray-200'
            }`}
          />

          <div className="grid grid-cols-3 gap-4">
            <select value={filterType} onChange={(e) => setFilterType(e.target.value as any)} className="px-4 py-2 rounded-lg border">
              <option value="all">All Types</option>
              <option value="projects">Projects</option>
              <option value="tasks">Tasks</option>
            </select>
            <select value={filterStatus} onChange={(e) => setFilterStatus(e.target.value)} className="px-4 py-2 rounded-lg border">
              <option value="all">All Statuses</option>
              <option value="active">Active</option>
              <option value="completed">Completed</option>
            </select>
            <select value={filterPriority} onChange={(e) => setFilterPriority(e.target.value)} className="px-4 py-2 rounded-lg border">
              <option value="all">All Priorities</option>
              <option value="high">High</option>
              <option value="medium">Medium</option>
              <option value="low">Low</option>
            </select>
          </div>
        </div>

        {loading && <p className="text-center">Searching...</p>}

        <div className="space-y-4">
          {results.length === 0 && searchTerm.length > 0 && !loading && (
            <p className={isDarkMode ? 'text-gray-400' : 'text-gray-600'}>No results found</p>
          )}

          {results.map((result) => (
            <div key={`${result.type}-${result.id}`} className={`p-4 rounded-lg ${isDarkMode ? 'bg-gray-800' : 'bg-white'}`}>
              <div className="flex items-center justify-between">
                <div>
                  <span className="text-sm font-semibold text-indigo-600">{result.type.toUpperCase()}</span>
                  <h3 className={`text-lg font-bold ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                    {result.name || result.title}
                  </h3>
                  {result.description && <p className="text-gray-600 text-sm">{result.description}</p>}
                </div>
                {result.status && <span className="text-sm font-medium text-gray-600">{result.status}</span>}
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Search;
