import React, { FC, useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { DarkModeContext } from '../context/DarkModeContext';
import { AuthContext } from '../context/AuthContext';
import { usersAPI } from '../services/api';

interface FormData {
  name: string;
  email: string;
  phone: string;
  department: string;
}

interface PasswordData {
  currentPassword: string;
  newPassword: string;
  confirmPassword: string;
}

interface User {
  id: string;
  name: string;
  email: string;
  phone?: string;
  department?: string;
  role?: string;
}

const Settings: FC = () => {
  const darkModeContext = useContext(DarkModeContext);
  const authContext = useContext(AuthContext);
  const navigate = useNavigate();

  if (!darkModeContext || !authContext) {
    return <div>Loading...</div>;
  }

  const { isDarkMode, toggleDarkMode } = darkModeContext;
  const { logout } = authContext;

  const [user, setUser] = useState<User | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [formData, setFormData] = useState<FormData>({
    name: '',
    email: '',
    phone: '',
    department: '',
  });
  const [passwordData, setPasswordData] = useState<PasswordData>({
    currentPassword: '',
    newPassword: '',
    confirmPassword: '',
  });
  const [activeTab, setActiveTab] = useState('profile');

  React.useEffect(() => {
    const userData = localStorage.getItem('user');
    if (userData) {
      const parsed = JSON.parse(userData);
      setUser(parsed);
      setFormData({
        name: parsed.name || '',
        email: parsed.email || '',
        phone: parsed.phone || '',
        department: parsed.department || '',
      });
    }
    setLoading(false);
  }, []);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  if (loading) return <div>Loading...</div>;

  return (
    <div className={`min-h-screen ${isDarkMode ? 'bg-gray-900' : 'bg-gray-50'}`}>
      <div className="max-w-4xl mx-auto px-4 py-8">
        <h1 className={`text-4xl font-bold mb-8 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>Settings</h1>

        <div className="grid grid-cols-4 gap-4 mb-8">
          {['profile', 'security', 'preferences', 'account'].map((tab) => (
            <button
              key={tab}
              onClick={() => setActiveTab(tab)}
              className={`p-3 rounded-lg font-semibold capitalize ${
                activeTab === tab
                  ? 'bg-indigo-600 text-white'
                  : isDarkMode
                  ? 'bg-gray-800 text-gray-300'
                  : 'bg-white text-gray-700'
              }`}
            >
              {tab}
            </button>
          ))}
        </div>

        {error && <div className="bg-red-100 text-red-700 p-4 rounded-lg mb-4">{error}</div>}
        {success && <div className="bg-green-100 text-green-700 p-4 rounded-lg mb-4">{success}</div>}

        {activeTab === 'profile' && (
          <div className={`${isDarkMode ? 'bg-gray-800' : 'bg-white'} p-6 rounded-lg`}>
            <h2 className={`text-2xl font-bold mb-4 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>Profile Settings</h2>
            <form className="space-y-4">
              <div>
                <label className={`block text-sm font-medium mb-2 ${isDarkMode ? 'text-gray-300' : 'text-gray-700'}`}>Name</label>
                <input type="text" value={formData.name} onChange={(e) => setFormData({...formData, name: e.target.value})} className="w-full px-4 py-2 border rounded-lg" />
              </div>
              <button className="px-6 py-2 bg-indigo-600 text-white rounded-lg">Save Changes</button>
            </form>
          </div>
        )}

        {activeTab === 'preferences' && (
          <div className={`${isDarkMode ? 'bg-gray-800' : 'bg-white'} p-6 rounded-lg`}>
            <h2 className={`text-2xl font-bold mb-4 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>Preferences</h2>
            <button onClick={toggleDarkMode} className="px-6 py-2 bg-indigo-600 text-white rounded-lg">
              Toggle Dark Mode
            </button>
          </div>
        )}

        {activeTab === 'account' && (
          <div className={`${isDarkMode ? 'bg-gray-800' : 'bg-white'} p-6 rounded-lg`}>
            <h2 className={`text-2xl font-bold mb-4 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>Account</h2>
            <button onClick={handleLogout} className="px-6 py-2 bg-red-600 text-white rounded-lg">
              Logout
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Settings;
