import React, { FC, useMemo } from 'react';

interface Task {
  _id?: string;
  id?: string;
  title: string;
  name?: string;
  startDate: string | Date;
  endDate: string | Date;
  dueDate?: string | Date;
  status?: 'pending' | 'in-progress' | 'completed' | 'on-hold';
  progress?: number;
  assignee?: string;
  priority?: 'low' | 'medium' | 'high';
  dependencies?: string[];
}

interface GanttChartProps {
  tasks: Task[];
  startDate: string | Date;
  endDate: string | Date;
}

const GanttChart: FC<GanttChartProps> = ({ tasks, startDate, endDate }) => {
  const projectStartDate = useMemo(() => new Date(startDate), [startDate]);
  const projectEndDate = useMemo(() => new Date(endDate), [endDate]);
  const totalDays = useMemo(() => {
    const diffTime = Math.abs(projectEndDate.getTime() - projectStartDate.getTime());
    return Math.ceil(diffTime / (1000 * 60 * 60 * 24)) + 1;
  }, [projectStartDate, projectEndDate]);

  const calculatePosition = (date: string | Date): number => {
    const taskDate = new Date(date);
    const diffTime = Math.abs(taskDate.getTime() - projectStartDate.getTime());
    return Math.ceil((diffTime / (1000 * 60 * 60 * 24)) * (100 / totalDays));
  };

  const calculateWidth = (start: string | Date, end: string | Date): number => {
    const startDate = new Date(start);
    const endDate = new Date(end);
    const diffTime = Math.abs(endDate.getTime() - startDate.getTime());
    const days = Math.max(1, Math.ceil(diffTime / (1000 * 60 * 60 * 24)));
    return (days * 100) / totalDays;
  };

  const getStatusColor = (status?: string): string => {
    switch (status?.toLowerCase()) {
      case 'completed':
        return 'bg-green-500';
      case 'in-progress':
        return 'bg-blue-500';
      case 'on-hold':
        return 'bg-yellow-500';
      case 'pending':
        return 'bg-gray-500';
      default:
        return 'bg-indigo-500';
    }
  };

  const getPriorityColor = (priority?: string): string => {
    switch (priority?.toLowerCase()) {
      case 'high':
        return 'border-l-4 border-red-500';
      case 'medium':
        return 'border-l-4 border-yellow-500';
      case 'low':
        return 'border-l-4 border-green-500';
      default:
        return 'border-l-4 border-gray-500';
    }
  };

  const formatDate = (date: string | Date): string => {
    return new Date(date).toLocaleDateString('en-US', {
      month: 'short',
      day: 'numeric',
      year: 'numeric',
    });
  };

  const today = new Date();
  const todayPosition = calculatePosition(today);

  return (
    <div className="w-full bg-white dark:bg-gray-900 rounded-lg shadow-lg overflow-hidden">
      {/* Header */}
      <div className="bg-gradient-to-r from-indigo-600 to-blue-600 text-white p-6">
        <h2 className="text-2xl font-bold mb-2">Project Timeline</h2>
        <p className="text-indigo-100">
          {formatDate(projectStartDate)} - {formatDate(projectEndDate)}
        </p>
      </div>

      {/* Content */}
      <div className="p-6">
        {tasks.length === 0 ? (
          <div className="text-center py-12">
            <p className="text-gray-500 dark:text-gray-400 text-lg">No tasks to display</p>
          </div>
        ) : (
          <div className="overflow-x-auto">
            <div className="min-w-full">
              {/* Timeline Header */}
              <div className="flex mb-4 bg-gray-100 dark:bg-gray-800 rounded-lg p-2">
                <div className="w-64 flex-shrink-0 pr-4">
                  <h3 className="font-semibold text-gray-700 dark:text-gray-300 text-sm">
                    Task Name
                  </h3>
                </div>
                <div className="flex-grow relative">
                  <div className="flex justify-between text-xs text-gray-600 dark:text-gray-400 px-4">
                    {Array.from({ length: Math.min(12, totalDays) }).map((_, i) => {
                      const date = new Date(projectStartDate);
                      date.setDate(date.getDate() + (i * Math.ceil(totalDays / 12)));
                      return (
                        <span key={i} className="text-center flex-1">
                          {date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' })}
                        </span>
                      );
                    })}
                  </div>
                </div>
              </div>

              {/* Timeline Grid */}
              <div className="space-y-3">
                {/* Today Line */}
                {todayPosition >= 0 && todayPosition <= 100 && (
                  <div className="absolute left-0 right-0 h-full pointer-events-none">
                    <div
                      className="absolute top-0 bottom-0 w-1 bg-red-500 opacity-50"
                      style={{ left: `calc(${todayPosition}% + 16rem)` }}
                    />
                  </div>
                )}

                {/* Task Bars */}
                {tasks.map((task) => {
                  const taskStart = task.startDate || task.dueDate;
                  const taskEnd = task.endDate || task.dueDate;

                  if (!taskStart || !taskEnd) return null;

                  const position = calculatePosition(taskStart);
                  const width = calculateWidth(taskStart, taskEnd);
                  const taskName = task.name || task.title || 'Untitled Task';
                  const progress = task.progress || 0;

                  return (
                    <div key={task._id || task.id} className="flex items-center gap-4">
                      {/* Task Info */}
                      <div className="w-64 flex-shrink-0">
                        <div
                          className={`${getPriorityColor(task.priority)} bg-white dark:bg-gray-800 rounded p-3 shadow hover:shadow-md transition-shadow cursor-pointer`}
                        >
                          <h4 className="font-semibold text-sm text-gray-900 dark:text-white truncate">
                            {taskName}
                          </h4>
                          <p className="text-xs text-gray-500 dark:text-gray-400 mt-1">
                            {formatDate(taskStart)} â†’ {formatDate(taskEnd)}
                          </p>
                          {task.assignee && (
                            <p className="text-xs text-gray-500 dark:text-gray-400 mt-1">
                              ðŸ‘¤ {task.assignee}
                            </p>
                          )}
                        </div>
                      </div>

                      {/* Timeline Bar */}
                      <div className="flex-grow relative h-16 bg-gray-50 dark:bg-gray-800 rounded-lg overflow-hidden">
                        {/* Background Grid */}
                        <div className="absolute inset-0 flex">
                          {Array.from({ length: Math.min(12, totalDays) }).map((_, i) => (
                            <div
                              key={i}
                              className="flex-1 border-r border-gray-200 dark:border-gray-700"
                            />
                          ))}
                        </div>

                        {/* Task Bar */}
                        <div
                          className={`absolute top-3 bottom-3 rounded-md shadow-md hover:shadow-lg transition-all ${getStatusColor(
                            task.status
                          )} opacity-90 hover:opacity-100 flex items-center px-3 group`}
                          style={{
                            left: `${position}%`,
                            width: `${width}%`,
                            minWidth: '40px',
                          }}
                        >
                          {/* Progress Bar */}
                          <div className="absolute inset-0 rounded-md overflow-hidden">
                            <div
                              className="h-full bg-white opacity-30"
                              style={{ width: `${progress}%` }}
                            />
                          </div>

                          {/* Progress Text */}
                          {progress > 0 && width > 40 && (
                            <span className="relative text-xs font-semibold text-white truncate">
                              {progress}%
                            </span>
                          )}

                          {/* Tooltip */}
                          <div className="absolute bottom-full left-0 mb-2 hidden group-hover:block bg-gray-900 dark:bg-gray-700 text-white text-xs rounded px-2 py-1 whitespace-nowrap z-10">
                            <p className="font-semibold">{taskName}</p>
                            <p>{formatDate(taskStart)} - {formatDate(taskEnd)}</p>
                            {task.status && <p>Status: {task.status}</p>}
                            <p>Progress: {progress}%</p>
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            </div>
          </div>
        )}

        {/* Legend */}
        <div className="mt-8 pt-6 border-t border-gray-200 dark:border-gray-700">
          <h3 className="font-semibold text-gray-900 dark:text-white mb-4">Legend</h3>
          <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
            <div className="flex items-center gap-2">
              <div className="w-6 h-3 bg-green-500 rounded"></div>
              <span className="text-sm text-gray-600 dark:text-gray-400">Completed</span>
            </div>
            <div className="flex items-center gap-2">
              <div className="w-6 h-3 bg-blue-500 rounded"></div>
              <span className="text-sm text-gray-600 dark:text-gray-400">In Progress</span>
            </div>
            <div className="flex items-center gap-2">
              <div className="w-6 h-3 bg-yellow-500 rounded"></div>
              <span className="text-sm text-gray-600 dark:text-gray-400">On Hold</span>
            </div>
            <div className="flex items-center gap-2">
              <div className="w-6 h-3 bg-gray-500 rounded"></div>
              <span className="text-sm text-gray-600 dark:text-gray-400">Pending</span>
            </div>
            <div className="flex items-center gap-2">
              <div className="w-1 h-6 border-l-4 border-red-500"></div>
              <span className="text-sm text-gray-600 dark:text-gray-400">High Priority</span>
            </div>
            <div className="flex items-center gap-2">
              <div className="w-1 h-6 border-l-4 border-yellow-500"></div>
              <span className="text-sm text-gray-600 dark:text-gray-400">Medium Priority</span>
            </div>
            <div className="flex items-center gap-2">
              <div className="w-1 h-6 border-l-4 border-green-500"></div>
              <span className="text-sm text-gray-600 dark:text-gray-400">Low Priority</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default GanttChart;
