import React, { FC, useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { projectsAPI, tasksAPI } from '../services/api';

interface ProjectDetail {
  _id: string;
  name: string;
  description: string;
  status: string;
  startDate: string;
  endDate: string;
  budget?: { estimated: number; actual: number };
}

const ProjectDetail: FC = () => {
  const { id } = useParams<{ id: string }>();
  const [project, setProject] = useState<ProjectDetail | null>(null);
  const [tasks, setTasks] = useState<any[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    if (id) {
      fetchProject();
      fetchTasks();
    }
  }, [id]);

  const fetchProject = async () => {
    try {
      const response = await projectsAPI.getOne(id!);
      setProject(response.data);
    } catch (err) {
      setError('Failed to load project');
    }
  };

  const fetchTasks = async () => {
    try {
      const response = await tasksAPI.getByProject(id!);
      setTasks(response.data);
    } catch (err) {
      console.error('Failed to load tasks');
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <div className="min-h-screen flex items-center justify-center">Loading...</div>;
  if (error) return <div className="min-h-screen flex items-center justify-center text-red-600">{error}</div>;
  if (!project) return <div className="min-h-screen flex items-center justify-center">Project not found</div>;

  return (
    <div className="min-h-screen bg-gray-50 p-8">
      <div className="max-w-6xl mx-auto">
        <h1 className="text-4xl font-bold text-gray-900 mb-2">{project.name}</h1>
        <span className="inline-block px-3 py-1 bg-blue-100 text-blue-800 rounded-full text-sm font-semibold">{project.status}</span>

        <div className="bg-white p-6 rounded-lg shadow mt-8 mb-8">
          <h2 className="text-2xl font-bold mb-4">Project Details</h2>
          <div className="grid grid-cols-2 gap-4">
            <div>
              <p className="text-gray-600 text-sm">Description</p>
              <p className="text-lg">{project.description}</p>
            </div>
            <div>
              <p className="text-gray-600 text-sm">Timeline</p>
              <p className="text-lg">{new Date(project.startDate).toLocaleDateString()} - {new Date(project.endDate).toLocaleDateString()}</p>
            </div>
          </div>
        </div>

        <h2 className="text-2xl font-bold mb-4">Tasks ({tasks.length})</h2>
        <div className="space-y-4">
          {tasks.length === 0 ? (
            <div className="bg-white p-8 rounded-lg text-center">
              <p className="text-gray-500">No tasks yet</p>
            </div>
          ) : (
            tasks.map((task) => (
              <div key={task._id} className="bg-white p-4 rounded-lg shadow">
                <h3 className="font-bold">{task.title}</h3>
                <p className="text-gray-600 text-sm">{task.description}</p>
                <span className="text-xs font-semibold text-indigo-600">{task.status}</span>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default ProjectDetail;
