import React, { FC, useState, useEffect, useContext, useCallback } from 'react';
import { DarkModeContext } from '../context/DarkModeContext';
import { AuthContext } from '../context/AuthContext';
import { tasksAPI } from '../services/api';

interface Subtask {
  title: string;
  completed?: boolean;
}

interface Comment {
  text: string;
  timestamp: string;
}

interface TaskItem {
  _id: string;
  title: string;
  description: string;
  status: string;
  priority: string;
  dueDate?: string;
  assignee?: string;
  subtasks?: Subtask[];
  comments?: Comment[];
  estimatedHours?: number;
  actualHours?: number;
}

interface NewTaskForm {
  title: string;
  description: string;
  status: string;
  priority: string;
  dueDate: string;
  assignee: string;
  subtasks: Subtask[];
  comments: Comment[];
  estimatedHours?: number;
  actualHours?: number;
}

interface AdminStats {
  byStatus?: Array<{ _id: string; count: number }>;
  byPriority?: Array<{ _id: string; count: number }>;
  overdueTasks?: number;
  overdueTasksList?: TaskItem[];
}

const Tasks: FC = () => {
  const [tasks, setTasks] = useState<TaskItem[]>([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('all');
  const [showModal, setShowModal] = useState(false);
  const [editingTask, setEditingTask] = useState<TaskItem | null>(null);
  const [, setExpandedTaskId] = useState<string | null>(null);
  const [showAdminPanel, setShowAdminPanel] = useState(false);
  const [newTask, setNewTask] = useState<NewTaskForm>({
    title: '',
    description: '',
    status: 'pending',
    priority: 'medium',
    dueDate: '',
    assignee: '',
    subtasks: [],
    comments: [],
  });
  const [adminStats, setAdminStats] = useState<AdminStats | null>(null);

  const darkModeContext = useContext(DarkModeContext);
  const authContext = useContext(AuthContext);

  const loadTasks = async () => {
    try {
      setLoading(true);
      const res = await tasksAPI.adminGetAll();
      setTasks(res.data || []);
    } catch (err) {
      console.error('Failed to load tasks:', err);
    } finally {
      setLoading(false);
    }
  };

  const loadAdminStats = useCallback(async () => {
    if (authContext?.user?.role !== 'admin') return;
    try {
      const res = await tasksAPI.adminGetStats();
      setAdminStats(res.data.stats);
    } catch (err) {
      console.error('Failed to load admin stats:', err);
    }
  }, [authContext?.user?.role]);

  useEffect(() => {
    loadTasks();
    if (authContext?.user?.role === 'admin') {
      loadAdminStats();
    }
  }, [authContext?.user?.role, loadAdminStats]);

  if (!darkModeContext || !authContext) {
    return <div>Loading...</div>;
  }

  const { isDarkMode } = darkModeContext;
  const { user } = authContext;

  const handleAddTask = async (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    if (!newTask.title.trim()) return;

    try {
      if (editingTask) {
        if (user?.role === 'admin') {
          await tasksAPI.adminUpdate(editingTask._id, newTask);
        } else {
          await tasksAPI.update(editingTask._id, newTask);
        }
      } else {
        await tasksAPI.create(newTask);
      }
      loadTasks();
      if (user?.role === 'admin') {
        loadAdminStats();
      }
      setNewTask({
        title: '',
        description: '',
        status: 'pending',
        priority: 'medium',
        dueDate: '',
        assignee: '',
        subtasks: [],
        comments: [],
      });
      setEditingTask(null);
      setShowModal(false);
    } catch (err) {
      console.error('Failed to save task:', err);
    }
  };

  const handleDeleteTask = async (taskId: string) => {
    if (window.confirm('Are you sure you want to delete this task?')) {
      try {
        if (user?.role === 'admin') {
          await tasksAPI.adminDelete(taskId);
        } else {
          await tasksAPI.delete(taskId);
        }
        loadTasks();
        if (user?.role === 'admin') {
          loadAdminStats();
        }
      } catch (err) {
        console.error('Failed to delete task:', err);
      }
    }
  };

  const handleEditTask = (task: TaskItem) => {
    setEditingTask(task);
    setNewTask({
      title: task.title,
      description: task.description,
      status: task.status,
      priority: task.priority,
      dueDate: task.dueDate || '',
      assignee: task.assignee || '',
      subtasks: task.subtasks || [],
      comments: task.comments || [],
      estimatedHours: task.estimatedHours,
      actualHours: task.actualHours,
    });
    setShowModal(true);
  };

  const getStatusColor = (status: string): string => {
    switch (status) {
      case 'completed':
        return 'bg-green-100 text-green-800';
      case 'in-progress':
        return 'bg-blue-100 text-blue-800';
      case 'pending':
        return 'bg-gray-100 text-gray-800';
      case 'overdue':
        return 'bg-red-100 text-red-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getPriorityColor = (priority: string): string => {
    switch (priority) {
      case 'critical':
        return 'text-red-600 bg-red-50';
      case 'high':
        return 'text-orange-600 bg-orange-50';
      case 'medium':
        return 'text-yellow-600 bg-yellow-50';
      case 'low':
        return 'text-green-600 bg-green-50';
      default:
        return 'text-gray-600 bg-gray-50';
    }
  };

  const getPriorityIcon = (priority: string): string => {
    switch (priority) {
      case 'critical':
        return 'üî¥';
      case 'high':
        return 'üü†';
      case 'medium':
        return 'üü°';
      case 'low':
        return 'üü¢';
      default:
        return '‚ö™';
    }
  };

  const filteredTasks = tasks.filter(task => {
    if (filter === 'all') return true;
    if (filter === 'overdue') return task.status === 'overdue';
    if (filter === 'in-progress') return task.status === 'in-progress';
    if (filter === 'completed') return task.status === 'completed';
    return true;
  });

  const completionPercentage = Math.round(
    (tasks.filter(t => t.status === 'completed').length / Math.max(tasks.length, 1)) * 100
  );

  return (
    <div className={`min-h-screen ${isDarkMode ? 'bg-gray-950' : 'bg-gray-50'}`}>
      <div className="max-w-7xl mx-auto px-4 py-8">
        <div className="flex justify-between items-start mb-8">
          <div>
            <h1 className={`text-4xl font-bold ${isDarkMode ? 'text-white' : 'text-gray-900'} mb-2`}>
              ‚úì Task Management {user?.role === 'admin' && <span className="text-sm bg-red-500 text-white px-2 py-1 rounded">Admin</span>}
            </h1>
            <p className={isDarkMode ? 'text-gray-400' : 'text-gray-600'}>
              Organize and track your project tasks efficiently
            </p>
          </div>
          <div className="flex gap-3">
            {user?.role === 'admin' && (
              <button
                onClick={() => setShowAdminPanel(!showAdminPanel)}
                className="px-6 py-3 bg-red-600 hover:bg-red-700 text-white font-semibold rounded-xl"
              >
                ‚öôÔ∏è Admin Panel
              </button>
            )}
            <button
              onClick={() => {
                setEditingTask(null);
                setNewTask({
                  title: '',
                  description: '',
                  status: 'pending',
                  priority: 'medium',
                  dueDate: '',
                  assignee: '',
                  subtasks: [],
                  comments: [],
                });
                setShowModal(true);
              }}
              className="px-6 py-3 bg-gradient-to-r from-indigo-600 to-purple-600 text-white font-semibold rounded-xl"
            >
              + Create Task
            </button>
          </div>
        </div>

        <div className="grid grid-cols-4 gap-4 mb-8">
          <div className={`${isDarkMode ? 'bg-gray-900' : 'bg-white'} rounded-xl p-6`}>
            <p className={isDarkMode ? 'text-gray-400' : 'text-gray-600'}>Total Tasks</p>
            <p className={`text-3xl font-bold ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>{tasks.length}</p>
          </div>
          <div className={`${isDarkMode ? 'bg-gray-900' : 'bg-white'} rounded-xl p-6`}>
            <p className={isDarkMode ? 'text-gray-400' : 'text-gray-600'}>In Progress</p>
            <p className="text-3xl font-bold text-blue-600">{tasks.filter(t => t.status === 'in-progress').length}</p>
          </div>
          <div className={`${isDarkMode ? 'bg-gray-900' : 'bg-white'} rounded-xl p-6`}>
            <p className={isDarkMode ? 'text-gray-400' : 'text-gray-600'}>Completed</p>
            <p className="text-3xl font-bold text-green-600">{tasks.filter(t => t.status === 'completed').length}</p>
          </div>
          <div className={`${isDarkMode ? 'bg-gray-900' : 'bg-white'} rounded-xl p-6`}>
            <p className={isDarkMode ? 'text-gray-400' : 'text-gray-600'}>Completion</p>
            <p className="text-3xl font-bold text-indigo-600">{completionPercentage}%</p>
          </div>
        </div>

        {showAdminPanel && adminStats && (
          <div className={`${isDarkMode ? 'bg-gray-900' : 'bg-white'} rounded-2xl p-6 mb-6`}>
            <h2 className={`text-2xl font-bold mb-4 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
              üìä Admin Panel
            </h2>
            <div className="grid grid-cols-2 gap-4">
              {adminStats.byStatus && (
                <div className={`${isDarkMode ? 'bg-gray-800' : 'bg-gray-50'} p-4 rounded-lg`}>
                  <p className={isDarkMode ? 'text-gray-400' : 'text-gray-600'}>By Status</p>
                  <div className="mt-2 space-y-1 text-sm">
                    {adminStats.byStatus.map((item) => (
                      <p key={item._id} className={isDarkMode ? 'text-gray-300' : 'text-gray-700'}>
                        {item._id}: {item.count}
                      </p>
                    ))}
                  </div>
                </div>
              )}
            </div>
          </div>
        )}

        <div className={`${isDarkMode ? 'bg-gray-900' : 'bg-white'} rounded-2xl p-6 mb-6`}>
          <div className="flex gap-2 flex-wrap">
            {[
              { id: 'all', label: 'All Tasks', icon: 'üìã' },
              { id: 'in-progress', label: 'In Progress', icon: '‚è≥' },
              { id: 'completed', label: 'Completed', icon: '‚úÖ' },
              { id: 'overdue', label: 'Overdue', icon: '‚ö†Ô∏è' },
            ].map(f => (
              <button
                key={f.id}
                onClick={() => setFilter(f.id)}
                className={`px-4 py-2 rounded-lg font-medium ${
                  filter === f.id
                    ? 'bg-indigo-600 text-white'
                    : isDarkMode
                    ? 'bg-gray-800 text-gray-200'
                    : 'bg-gray-100'
                }`}
              >
                {f.icon} {f.label}
              </button>
            ))}
          </div>
        </div>

        {loading ? (
          <div className="text-center py-12">Loading...</div>
        ) : filteredTasks.length > 0 ? (
          <div className="space-y-4">
            {filteredTasks.map(task => (
              <div key={task._id} className={`${isDarkMode ? 'bg-gray-900' : 'bg-white'} rounded-xl p-6`}>
                <div className="flex items-start justify-between">
                  <div className="flex-1">
                    <div className="flex items-center gap-3 mb-2">
                      <span className={`px-3 py-1 rounded-full text-xs font-bold ${getStatusColor(task.status)}`}>
                        {task.status}
                      </span>
                      <span className={`px-3 py-1 rounded-full text-xs font-bold ${getPriorityColor(task.priority)}`}>
                        {getPriorityIcon(task.priority)} {task.priority}
                      </span>
                    </div>
                    <h3 className={`text-xl font-bold ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
                      {task.title}
                    </h3>
                    <p className={`text-sm mt-2 ${isDarkMode ? 'text-gray-400' : 'text-gray-600'}`}>
                      {task.description}
                    </p>
                  </div>
                  <div className="flex gap-2 ml-4">
                    <button
                      onClick={() => handleEditTask(task)}
                      className={`px-4 py-2 rounded-lg ${isDarkMode ? 'bg-gray-800' : 'bg-gray-100'}`}
                    >
                      ‚úèÔ∏è Edit
                    </button>
                    <button
                      onClick={() => handleDeleteTask(task._id)}
                      className="px-4 py-2 rounded-lg bg-red-600 text-white"
                    >
                      üóëÔ∏è Delete
                    </button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div className={`text-center py-12 rounded-xl ${isDarkMode ? 'bg-gray-900' : 'bg-gray-50'}`}>
            <p className={isDarkMode ? 'text-gray-400' : 'text-gray-600'}>No tasks found</p>
          </div>
        )}
      </div>

      {showModal && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className={`${isDarkMode ? 'bg-gray-900' : 'bg-white'} rounded-2xl max-w-2xl w-full p-6`}>
            <h2 className={`text-2xl font-bold mb-6 ${isDarkMode ? 'text-white' : 'text-gray-900'}`}>
              {editingTask ? 'Edit Task' : 'Create New Task'}
            </h2>

            <form onSubmit={handleAddTask} className="space-y-4">
              <input
                type="text"
                placeholder="Task Title"
                value={newTask.title}
                onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
                className={`w-full px-4 py-2 rounded-lg border ${isDarkMode ? 'bg-gray-800 border-gray-700 text-white' : 'bg-gray-50 border-gray-200'}`}
                required
              />

              <textarea
                placeholder="Description"
                value={newTask.description}
                onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
                className={`w-full px-4 py-2 rounded-lg border ${isDarkMode ? 'bg-gray-800 border-gray-700 text-white' : 'bg-gray-50 border-gray-200'}`}
                rows={3}
              />

              <div className="grid grid-cols-3 gap-4">
                <select
                  value={newTask.status}
                  onChange={(e) => setNewTask({ ...newTask, status: e.target.value })}
                  className={`px-4 py-2 rounded-lg border ${isDarkMode ? 'bg-gray-800 border-gray-700 text-white' : 'bg-gray-50 border-gray-200'}`}
                >
                  <option value="todo">To Do</option>
                  <option value="in-progress">In Progress</option>
                  <option value="completed">Completed</option>
                </select>

                <select
                  value={newTask.priority}
                  onChange={(e) => setNewTask({ ...newTask, priority: e.target.value })}
                  className={`px-4 py-2 rounded-lg border ${isDarkMode ? 'bg-gray-800 border-gray-700 text-white' : 'bg-gray-50 border-gray-200'}`}
                >
                  <option value="low">Low</option>
                  <option value="medium">Medium</option>
                  <option value="high">High</option>
                </select>

                <input
                  type="date"
                  value={newTask.dueDate}
                  onChange={(e) => setNewTask({ ...newTask, dueDate: e.target.value })}
                  className={`px-4 py-2 rounded-lg border ${isDarkMode ? 'bg-gray-800 border-gray-700 text-white' : 'bg-gray-50 border-gray-200'}`}
                />
              </div>

              <div className="flex gap-4 pt-4">
                <button
                  type="submit"
                  className="flex-1 px-6 py-3 bg-indigo-600 text-white font-semibold rounded-xl"
                >
                  {editingTask ? 'Update Task' : 'Create Task'}
                </button>
                <button
                  type="button"
                  onClick={() => setShowModal(false)}
                  className={`flex-1 px-6 py-3 rounded-xl font-semibold ${isDarkMode ? 'bg-gray-800' : 'bg-gray-200'}`}
                >
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
};

export default Tasks;
